#!/usr/bin/env bash

tee -a ~/.ssh/config << END
Host *
    StrictHostKeyChecking no
END

sudo chmod 400 ~/.ssh/config

tee -a ~/.ssh/authorized_keys << END
ssh-rsa  ubuntu@master1
ssh-rsa  ubuntu@node1
ssh-rsa  ubuntu@master2
ssh-rsa  ubuntu@node2
ssh-rsa  ubuntu@master3
ssh-rsa  ubuntu@node3
ssh-rsa  ubuntu@jumphost
ssh-rsa  ubuntu@operations
END
